package br.com.sge.util;import java.sql.Connection;import java.sql.SQLException;import br.com.topsys.database.TSDataBaseBrokerIf;import br.com.topsys.database.factory.TSDataBaseBrokerFactory;/** *  *  *  * @author Marcelo Silva */public class ConnectionFactory {	private ConnectionFactory() {	}	public static Connection getConnection() {		TSDataBaseBrokerIf broker = TSDataBaseBrokerFactory.getDataBaseBrokerIf();		return broker.getConnection();	}	public static void closeConnection(Connection con) {		try {			if (con != null) {				con.close();			}		} catch (SQLException ex) {			ex.printStackTrace();		}	}}